export default context => {

    // 因为有可能会是异步路由钩子函数或组件，所以我们将返回一个 Promise，
    // 以便服务器能够等待所有的内容在渲染前就已经准备就绪。
    return new Promise((resolve, reject) => {

        const { app, router, store } = createApp();

        // 依据当前请求地址 设置服务器端 router
        router.push(context.url);

        // 等到 router 将可能的异步组件和钩子函数解析完
        router.onReady(() => {

            const matched = router.getMatchedComponents();

            // 匹配不到组件时，reject，并返回 404
            if (!matched.length) {
                return reject({
                    code: 404,
                    msg: '未找到匹配的组件',
                    url: context.url,
                });
            }

            // 遍历直接匹配到的组件，查找其子组件/后辈组件，得到匹配到的全部组件
            let matchedComponents = [];
            let matchedComponentNames = [];
            const recursive = (component, name) => {
                if (matchedComponentNames.indexOf(name) !== -1) return;

                matchedComponentNames.push(name);
                matchedComponents.push(component);
                if (component.components) {
                    Object.keys(component.components).forEach(key => {
                        recursive(component.components[key], component.components[key].name);
                        // recursive(component.components[key], key);
                    });
                }
            };
            matched.map(component => {
                recursive(component, component.name);
            });

            // console.log('匹配到的组件有: ' + matchedComponentNames.join(', '));

            // 调用匹配到的组件上的 asyncData 方法
            Promise
            .all(
                matchedComponents
                .filter(
                    component => typeof component.asyncData === 'function'
                )
                .map(
                    component => component.asyncData
                )
                .map(
                    hook => hook({
                        store,
                        route: router.currentRoute,
                    })
                )
            )
            .then(
                () => {

                    // 此时，state 已准备就绪，将其同步到要给到浏览器的输出中
                    // 状态将自动序列化为 `window.__INITIAL_STATE__`，并注入 HTML。
                    context.state = store.state;

                    resolve(app);
                }
            )
            .catch(
                err => {

                    // reject(app);
                    reject(err);
                }
            );

        }, reject);

    });

};

