		},
		toggleMenuA(){
			if(this.menuA){
				this.menuA = false;
			}else{
				this.menuA = true;
				this.menuB = false;
				this.menuC = false;

				this.searchBar = false;
			}
		},
		toggleMenuB(){
			if(this.menuB){
