import nativeInvoke from './srs-js-bridge.js';

function createRandomName(){
	return 'mdb'+ (new Date().getTime()) + Math.ceil(Math.random()*1000);
}

export default {

	// 获取设备信息  设备名称 + 设备类型 + 系统类型 + 系统版本 + 设备ID
	getDeviceInfo(callback){
		let cbName = createRandomName();
		window[cbName] = function(result){
			console.log(result);
			delete window[cbName];
			typeof callback === 'function' && callback({
				osName: result.systemName, // 操作系统名称
				osVersion: result.deviceVersion, // 操作系统版本
				name: result.deviceName, // 设备名称
				model: result.deviceModel, // 设备型号
				id: result.deviceID, // 设备ID(由APP生成)
			});
		};
		nativeInvoke('JSDeviceInformation', null, cbName);
	},

	// 获取APP信息
	getAppInfo(callback){
		let cbName = createRandomName();
		window[cbName] = function(result){
			console.log(result);
			delete window[cbName];
			typeof callback === 'function' && callback({
				version: result.appVersion, // 应用版本号
				buildVersion: result.buildVersion, // 构建版本号
			});
		};
		nativeInvoke('JSAppInformation', null, cbName);
	},

	// 放入剪贴板
	copy(content){
		nativeInvoke(
			'JSPasteboardFunction',
			{
				name: typeof content === 'string' ? content : '',
			},
			function(result){
				console.log(result);
				typeof callback === 'function' && callback(result);
			}
		);
	},

	// 获取位置信息
	getLocationInfo(callback){
		let cbName = createRandomName();
		window[cbName] = function(result){
			console.log(result);
			delete window[cbName];
			let err;
			let info;
			switch(result.locationStatus || result.loactionStatus){
				case '-1':
					err = {
						code: '-1',
						msg: '定位功能未开启'
					}
					break;
				case '0':
					err = {
						code: '0',
						msg: '城市信息获取失败'
					}
					break;
				case '1':
					err = null;
					info  = {
						cityName: result.city,
						cityCode: result.cityCode,
						longitude: result.longitude,
						latitude: result.latitude,
					}
					break;
			}
			typeof callback === 'function' && callback(err, info);
		};
		nativeInvoke('JSLocationCityInformation', null, cbName);
	},

	// 分享
	share(info, callback){
		let cbName = createRandomName();
		window[cbName] = function(result){
			console.log(result);
			delete window[cbName];
			typeof callback === 'function' && callback({
				version: result.appVersion, // 应用版本号
				buildVersion: result.buildVersion, // 构建版本号
			});
		};
		nativeInvoke('JSAppShare', null, cbName);
	},

	// 获取电话所有者信息
	getPhoneOwnerInfo(){
		let cbName = createRandomName();
		window[cbName] = function(result){
			console.log(result);
			delete window[cbName];
			typeof callback === 'function' && callback({
				version: result.appVersion, // 应用版本号
				buildVersion: result.buildVersion, // 构建版本号
			});
		};
		nativeInvoke('JSAddressBook', null, cbName);
	},

	alert(settings){
		window.alert(settings.content, settings.title, settings.confirmBtnText);
		typeof settings.onConfirm === 'function' && settings.onConfirm();
		// setTimeout(function(){
		// 	typeof settings.onConfirm === 'function' && settings.onConfirm();
		// },0);
	},
	// a:function(){
	// 	console.log('a');
	// },
	// b:function(){
	// 	console.log('b');
	// },
};
